import DataViewer from '../../component/ui/DataViewer';
import { useParams } from 'react-router';
import { Link as RouteLink } from 'react-router-dom';
import LinkButton from '../../component/ui/LinkButton';
import Card from '../../component/ui/Card';
import Stack from '../../component/ui/Stack';
import useOrganization from '../../hook/useOrganization';

const schema = [
    {
        text: "Информация",
        tabs: [
            { name: "name", text: "Название" },
        ]
    },
    {
        text: "Администратор",
        tabs: [
            { name: "administratorFullName", text: "Имя" },
        ]
    },
    {
        text: "Контакты",
        tabs: [
            { name: "email", text: "Почта" },
            { name: "phoneNumber", text: "Телефон" },
            { name: "address", text: "Адрес" },
        ]
    },
];

function OrganizationViewer({ searchable }) {
    const { email } = useParams();
    const organization = useOrganization(email);

    if (!organization) {
        return;
    }

    const avatarURL = organization.avatarURL || 'data:image/png;base64,';
    return (
        <Card padding="m">
            <Stack direction="horizontal" gap="m">
                <Stack gap="sm">
                    <img width="150" height="150" src={avatarURL} alt="Фотография организации." />
                    <LinkButton style="primary">
                        <RouteLink to={`/home/edit_org/${email}`}>Редактировать</RouteLink>
                    </LinkButton>
                    <LinkButton style="secondary">
                        <RouteLink to={`/home/delete_org/${email}`}>Удалить</RouteLink>
                    </LinkButton>
                </Stack>
                <DataViewer
                    path={searchable && 'organizations'}
                    title={`${organization.name}`}
                    entity={organization}
                    schema={schema} />
            </Stack>
        </Card>
    )
}

export default OrganizationViewer